    // START_CONTRACT: BuildRibbon
    //   PURPOSE: Build ribbon.
    //   INPUTS: none
    //   OUTPUTS: { bool - true when method can build ribbon }
    //   SIDE_EFFECTS: May modify CAD entities, configuration files, runtime state, or diagnostics.
    //   LINKS: M-RIBBON
    // END_CONTRACT: BuildRibbon

    public bool BuildRibbon()
    {
        // START_BLOCK_BUILD_RIBBON
        RibbonControl? ribbon = ComponentManager.Ribbon;
        if (ribbon is null)
        {
            _log.Write("Р В Р’В Р Р†Р вЂљРЎвЂќР В Р’В Р вЂ™Р’ВµР В Р’В Р В РІР‚В¦Р В Р Р‹Р Р†Р вЂљРЎв„ўР В Р’В Р вЂ™Р’В° Р В Р’В Р В РІР‚В¦Р В Р’В Р вЂ™Р’ВµР В Р’В Р СћРІР‚ВР В Р’В Р РЋРІР‚СћР В Р Р‹Р В РЎвЂњР В Р Р‹Р Р†Р вЂљРЎв„ўР В Р Р‹Р РЋРІР‚СљР В Р’В Р РЋРІР‚вЂќР В Р’В Р В РІР‚В¦Р В Р’В Р вЂ™Р’В°.");
            return false;
        }

        RibbonTab? existing = ribbon.Tabs.FirstOrDefault(t => t.Id == TabId);
        if (existing is not null)
        {
            ribbon.Tabs.Remove(existing);
        }

        var tab = new RibbonTab { Id = TabId, Title = "Р В Р’В Р вЂ™Р’В­Р В Р’В Р РЋРІР‚С”Р В Р’В Р РЋРЎв„ў Р В Р’В Р РЋРЎСџР В Р’В Р вЂ™Р’В Р В Р’В Р РЋРІР‚С”" };
        tab.Panels.Add(BuildBasePanel());
        tab.Panels.Add(BuildExcelPanel());
        tab.Panels.Add(BuildControlPanel());
        ribbon.Tabs.Add(tab);
        _log.Write("Р В Р’В Р Р†Р вЂљРЎвЂќР В Р’В Р вЂ™Р’ВµР В Р’В Р В РІР‚В¦Р В Р Р‹Р Р†Р вЂљРЎв„ўР В Р’В Р вЂ™Р’В° ElTools Р В Р Р‹Р В РЎвЂњР В Р’В Р РЋРІР‚СћР В Р’В Р вЂ™Р’В·Р В Р’В Р СћРІР‚ВР В Р’В Р вЂ™Р’В°Р В Р’В Р В РІР‚В¦Р В Р’В Р вЂ™Р’В°.");
        return true;
        // END_BLOCK_BUILD_RIBBON
    }

