    // START_CONTRACT: CreateButton
    //   PURPOSE: Create button.
    //   INPUTS: { text: string - method parameter; command: string - method parameter; tooltip: string - method parameter }
    //   OUTPUTS: { RibbonButton - result of create button }
    //   SIDE_EFFECTS: May modify CAD entities, configuration files, runtime state, or diagnostics.
    //   LINKS: M-RIBBON
    // END_CONTRACT: CreateButton

    private static RibbonButton CreateButton(string text, string command, string tooltip)
    {
        // START_BLOCK_CREATE_RIBBON_BUTTON
        return new RibbonButton
        {
            Text = text,
            ShowText = true,
            ToolTip = tooltip,
            CommandParameter = CreateCommandMacro(command),
            CommandHandler = new RibbonCommandHandler()
        };
        // END_BLOCK_CREATE_RIBBON_BUTTON
    }

    // START_CONTRACT: CreateCommandMacro
    //   PURPOSE: Create command macro.
    //   INPUTS: { command: string - method parameter }
    //   OUTPUTS: { string - textual result for create command macro }
    //   SIDE_EFFECTS: May modify CAD entities, configuration files, runtime state, or diagnostics.
    //   LINKS: M-RIBBON
    // END_CONTRACT: CreateCommandMacro

    private static string CreateCommandMacro(string command)
    {
        // START_BLOCK_CREATE_COMMAND_MACRO
        return $"_.{command} ";
        // END_BLOCK_CREATE_COMMAND_MACRO
    }

    private sealed class RibbonCommandHandler : ICommand
    {
        public event EventHandler? CanExecuteChanged
        {
            add { }
            remove { }
        }

        // START_CONTRACT: CanExecute
        //   PURPOSE: Check whether execute.
        //   INPUTS: { parameter: object? - method parameter }
        //   OUTPUTS: { bool - true when method can check whether execute }
        //   SIDE_EFFECTS: Reads CAD/runtime/config state and may emit diagnostics.
        //   LINKS: M-RIBBON
        // END_CONTRACT: CanExecute

        public bool CanExecute(object? parameter)
        {
            // START_BLOCK_RIBBON_CAN_EXECUTE
            return true;
            // END_BLOCK_RIBBON_CAN_EXECUTE
        }

        // START_CONTRACT: Execute
        //   PURPOSE: Execute.
        //   INPUTS: { parameter: object? - method parameter }
        //   OUTPUTS: { void - no return value }
        //   SIDE_EFFECTS: May modify CAD entities, configuration files, runtime state, or diagnostics.
        //   LINKS: M-RIBBON
        // END_CONTRACT: Execute

        public void Execute(object? parameter)
        {
            // START_BLOCK_RIBBON_EXECUTE
            if (parameter is not string command)
            {
                return;
            }

            Document? doc = Application.DocumentManager.MdiActiveDocument;
            if (doc is not null)
            {
                string macro = command.EndsWith(" ", StringComparison.Ordinal) ? command : command + " ";
                doc.SendStringToExecute(macro, true, false, false);
            }
            // END_BLOCK_RIBBON_EXECUTE
        }
    }
